const cardsData = [
      {
      name: "Line Charts",
      textfront: "Composed of a single line, that connects the closing prices at each time intervals.",
      textback: "Very simple type of chart, may be too simple for day traders. Often used in presentations and reports. Commonly used also to draw trend lines to anticipate potential price inflections.",
      bgcolor: 234567,
      },
      {
      name: "Candlestick Charts",
      textfront: "",
      textback: "The candlestick is composed of 3 parts, the body, the upper tail and the lower tail. The body goes from the opening to the closing price. The upper and lower tails are thin lines that extend from the highest to the lowest prices reached. A green candle indicates a higher closing price than the opening one, a red one indicates a lower one.",
      bgcolor: 543267,
      },
      {
      name: "Bar Charts",
      textfront: "",
      textback: "",
      bgcolor: 324567,
      },
      {
      name: "Point and Figures Charts",
      textfront: "",
      textback: "",
      bgcolor: 444466,
      },

  ]


export default cardsData;
