import "../App.css"

import React from "react";
import ReactCardFlip from 'react-card-flip';
import CardFront from "./CardFront"
import CardBack from "./CardBack"

class Card extends React.Component {
  constructor() {
    super();
      this.state = {
      isFlipped: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    e.preventDefault();
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    return (
      <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="horizontal">
        <div class="card-side card-front">
          <CardFront name={this.props.name} textfront={this.props.textfront}/>
          <button class="flip-button" onClick={this.handleClick}>Click to flip</button>
        </div>

        <div class="card-side card-back">
          <CardBack name={this.props.name} textback={this.props.textback}/>
          <button class='flip-button' onClick={this.handleClick}>Click to flip</button>
        </div>
        <p>Tap or click to flip</p>
      </ReactCardFlip>
      )
    };
};

export default Card;
